sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

	var notes = current.comments.getJournalEntry(1).split('(Comments)')[1];
	template.print('<p style="white-space:pre-wrap;color:#000000;"><font color="#000000" face="helvetica">' + notes + '</font></p>');
	//white-space: pre-wrap;
})(current, template, email, email_action, event);
